import React, { Component } from 'react';

class Counter extends Component {
    state= {
        number: 0
    }
    render() {
        const {number} = this.state;
        return (
            <div>
                <h1>{number}</h1>
                <button
                    onClick={()=>{
                        this.setState(prevState => {
                            return {
                                number: prevState.number + 1
                            }
                        })
                        this.setState(prevState => ({
                            number: prevState.number +1
                        }))
                    }}
                >
                    +1
                </button>
            </div>
        );
    }
}

export default Counter;
